function onChange(control, oldValue, newValue, isLoading, isTemplate) {
   if (isLoading || newValue === '') {
      return;
   }
	var ajax=new GlideAjax("GetEmailBySysid");
	ajax.addParam("sysparm_name","getEmail");
	ajax.addParam("sysparm_sysid",g_form.getValue("assigned_to"));
	console.log(g_form.getValue("assigned_to"));
	ajax.getXML(SetValueEmail);
	function SetValueEmail(response)
	{
		var result=response.responseXML.documentElement.getAttribute("answer");
		g_form.setValue("short_description",result);
	}
   //Type appropriate comment here, and begin script below

}
