sys_script_include.script:
var GetEmailBySysid = Class.create();
GetEmailBySysid.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {

	getEmail:function()
	{
		gs.info("AAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAA");
		var sysid=this.getParameter("sysparm_sysid");
		gs.info(sysid);
		var user=new GlideRecord("sys_user");
		user.addQuery("sys_id",sysid);
		user.query();
		user.next();
		return user.email;
	},
    type: 'GetEmailBySysid'
});
